import './App.css';
import BookForm from './components/BookForm'

import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Book from './Book';

function App() {
  return (
    <Router>
      <div className="App">
        <h1>Books</h1>
        <Switch>
          <Route exact path="/">
            <BookForm />
          </Route>
          <Route path="/book/:bookName">
            <Book />
          </Route>
          <Route path="*">
            <h1>404</h1>
            <p>This is not the webpage you are looking for.</p>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
